import React, { useState } from 'react';
import './App.css'; // Import your CSS file

function App() {
    const [recipientName, setRecipientName] = useState('');
    const [messageTemplate, setMessageTemplate] = useState('');
    const [generatedEmail, setGeneratedEmail] = useState('');

    const generateEmail = () => {
        // Implement your logic to generate the email here (e.g., API call)
        const generatedEmailContent = `Hello ${recipientName},\n\n${messageTemplate}`;

        // Set the generated email content
        setGeneratedEmail(generatedEmailContent);
    };

    return (
        <div className="container">
            <h1>Email Outreach Generator</h1>
            <div className="form">
                <label htmlFor="recipientName">Recipient's Name:</label>
                <input
                    type="text"
                    id="recipientName"
                    value={recipientName}
                    onChange={(e) => setRecipientName(e.target.value)}
                    placeholder="Enter recipient's name"
                />
                <label htmlFor="messageTemplate">Message Template:</label>
                <textarea
                    id="messageTemplate"
                    value={messageTemplate}
                    onChange={(e) => setMessageTemplate(e.target.value)}
                    rows="4"
                    placeholder="Enter your message template"
                />
                <button onClick={generateEmail}>Generate Email</button>
            </div>
            <div className="result">
                <h2>Generated Email:</h2>
                <div id="generatedEmail">{generatedEmail}</div>
            </div>
        </div>
    );
}

export default App;
