import React, { useState } from 'react';
import {
    Container,
    Paper,
    Typography,
    Grid,
    TextField,
    Button,
    List,
    ListItem,
    ListItemText,
    IconButton,
    Box,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import './App.css';

function App() {
    const [prompt, setPrompt] = useState('');
    const [recipientNames, setRecipientNames] = useState([]);
    const [newRecipient, setNewRecipient] = useState('');
    const [generatedEmail, setGeneratedEmail] = useState('');

    const addRecipient = () => {
        if (newRecipient) {
            setRecipientNames([...recipientNames, newRecipient]);
            setNewRecipient('');
        }
    };

    const removeRecipient = (index) => {
        const updatedRecipientNames = [...recipientNames];
        updatedRecipientNames.splice(index, 1);
        setRecipientNames(updatedRecipientNames);
    };

    const generateEmail = () => {
        // Implement the logic to generate emails based on the prompt and recipients.
        // For now, we'll just set a placeholder email.
        setGeneratedEmail('Generated email content will appear here.');
    };

    return (
      <Container maxWidth="md">
          <Paper elevation={3} className="paper">
              <Typography variant="h4" gutterBottom className="heading">
                  Personalized Email Outreach
              </Typography>
              <TextField
                  id="prompt"
                  label="Enter Your Prompt"
                  variant="outlined"
                  fullWidth
                  multiline
                  rows={4}
                  value={prompt}
                  onChange={(e) => setPrompt(e.target.value)}
                  className="prompt-input"
              />
              <Grid container spacing={2} alignItems="center" className="recipient-grid">
                  <Grid item xs={8}>
                      <TextField
                          id="newRecipient"
                          label="Add Recipient"
                          variant="outlined"
                          fullWidth
                          value={newRecipient}
                          onChange={(e) => setNewRecipient(e.target.value)}
                          className="recipient-input"
                      />
                  </Grid>
                  <Grid item xs={4}>
                      <Button
                          variant="contained"
                          color="primary"
                          onClick={addRecipient}
                          fullWidth
                          className="add-button"
                      >
                          Add Recipient
                      </Button>
                  </Grid>
              </Grid>
              {recipientNames.length > 0 && (
                  <List className="recipient-list">
                      {recipientNames.map((name, index) => (
                          <ListItem key={index} className="recipient-item">
                              <Chip
                                  label={name}
                                  onDelete={() => removeRecipient(index)}
                                  color="primary"
                                  className="recipient-chip"
                              />
                          </ListItem>
                      ))}
                  </List>
              )}
              <Button
                  variant="contained"
                  color="primary"
                  onClick={generateEmail}
                  fullWidth
                  className="generate-button"
              >
                  Generate Email
              </Button>
              <Box mt={2} className="generated-email-box">
                  <Typography variant="h6" className="generated-email-heading">Generated Email:</Typography>
                  <Typography variant="body1" className="generated-email-content">{generatedEmail}</Typography>
              </Box>
          </Paper>
      </Container>
  );
}

export default App;
