import React, { useState } from 'react';
import { Container, Paper, Typography, Box, Button } from '@mui/material';

function App() {
    const [prompt, setPrompt] = useState('');
    const [generatedEmail, setGeneratedEmail] = useState(''); // New state for generated email

    const generateEmail = () => {
        // Make a POST request to your FastAPI backend to generate the email
        fetch('http://localhost:8000/generate-email', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ prompt: prompt }),
        })
            .then((response) => response.json())
            .then((data) => {
                // Update the generated email in the state
                responseEmail = data.generated_email
                setGeneratedEmail(data.generated_email);
            })
            .catch((error) => {
                console.error(error);
            });
    };

    return (
        <Container maxWidth="md" className="page-container">
            <Paper elevation={3} className="paper">
                <Typography variant="h4" gutterBottom className="heading">
                    <span className="headline-text">Personalized Email Outreach</span>
                </Typography>
                <PromptInput prompt={prompt} setPrompt={setPrompt} />
                <Button
                    variant="contained"
                    color="primary"
                    onClick={generateEmail}
                    fullWidth
                    className="generate-button"
                >
                    Generate Email
                </Button>
                <GeneratedEmail generatedEmail={responseEmail} />
            </Paper>
        </Container>
    );
}
