import React, { useState } from 'react';
import { Container, Paper, Typography, Box, Button, IconButton } from '@mui/material';
import PromptInput from './components/PromptInput';
import RecipientList from './components/RecipientList';
import GeneratedEmail from './components/GeneratedEmail';
import './App.css';
import Header from './components/Header';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
//import { BrowserRouter as Router, Route, Link, Routes, usenavigation } from 'react-router-dom'; // Import Router and related components
import GeneratedEmailPage from './components/GeneratedEmailPage'; // Import the new component
import { BrowserRouter, Route, Routes, useNavigate } from "react-router-dom";


function App() {
    const [prompt, setPrompt] = useState('');
    const [recipientNames, setRecipientNames] = useState([]);
    const [newRecipient, setNewRecipient] = useState('');
    const [generatedEmail, setGeneratedEmail] = useState('');



    const addRecipient = () => {
        if (newRecipient) {
            setRecipientNames([...recipientNames, newRecipient]);
            setNewRecipient('');
        }
    };

    const removeRecipient = (index) => {
        const updatedRecipientNames = [...recipientNames];
        updatedRecipientNames.splice(index, 1);
        setRecipientNames(updatedRecipientNames);
    };

    /*
    const generateEmail = async () => {
        // Prepare the data to send to the backend
        const data = {
            prompt: prompt,
        };

        // Make a POST request to your FastAPI backend
        const response = await fetch('http://localhost:8000/generate-email', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        });

        if (response.ok) {
            const responseData = await response.json();
            setGeneratedEmail(responseData.generated_email); // Update generatedEmail with data from the backend

            // Navigate to the new page
            window.location.href = '/generated-email';
        } else {
            // Handle errors here
            console.error('Failed to generate email');
        }
    };
    */
    const navigate = useNavigate(); // Hook to navigate

    const generateEmail = async () => {
        // Your logic to call the backend and generate the email
        const data = { prompt: prompt };

        // Simulating a backend call with a timeout
        setTimeout(() => {
            // Suppose this is the response from your backend
            const responseData = { generated_email: "Email content generated from the prompt." };

            setGeneratedEmail(responseData.generated_email);
            // Navigate to the new page after setting the state
            navigate('/generated-email');
        }, 1000);
    };

    return (
        <BrowserRouter>
            <Header />
            <Routes>
                <Route path="/" element={
                    <Container maxWidth="md" className="page-container">
                        <Paper elevation={3} className="paper">
                            <PromptInput prompt={prompt} setPrompt={setPrompt} />
                            <div className="generate-button-div">
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={generateEmail}
                                    className="generate-button"
                                    startIcon={<ArrowForwardIcon />}
                                >
                                    Generate Email
                                </Button>
                            </div>

                        </Paper>
                    </Container>
                } />
                <Route path="/generated-email" element={<GeneratedEmailPage emailContent={generatedEmail} />} />
            </Routes>
        </BrowserRouter>
    );
}

export default App;
