import React, { useState } from 'react';
import {
    Container,
    Paper,
    Typography,
    Grid,
    TextField,
    Button,
    List,
    ListItem,
    ListItemText,
    IconButton,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import './App.css';

function App() {
    const [prompt, setPrompt] = useState('');
    const [recipientNames, setRecipientNames] = useState([]);
    const [newRecipient, setNewRecipient] = useState('');
    const [verificationError, setVerificationError] = useState('');

    const addRecipient = () => {
        if (newRecipient) {
            setRecipientNames([...recipientNames, newRecipient]);
            setNewRecipient('');
        }
    };

    const removeRecipient = (index) => {
        const updatedRecipientNames = [...recipientNames];
        updatedRecipientNames.splice(index, 1);
        setRecipientNames(updatedRecipientNames);
    };

    const generateEmail = () => {
        // Implement the logic to generate emails based on the prompt and recipients
        // For now, we'll just clear the recipient names
        setRecipientNames([]);
    };

    return (
        <Container maxWidth="md">
            <Paper elevation={3} className="paper">
                <Typography variant="h4">Email Outreach Generator</Typography>
                <TextField
                    id="prompt"
                    label="Enter Your Prompt"
                    variant="outlined"
                    fullWidth
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                />
                <Grid container spacing={2}>
                    <Grid item xs={8}>
                        <TextField
                            id="newRecipient"
                            label="Add Recipient"
                            variant="outlined"
                            fullWidth
                            value={newRecipient}
                            onChange={(e) => setNewRecipient(e.target.value)}
                        />
                    </Grid>
                    <Grid item xs={4}>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={addRecipient}
                            fullWidth
                        >
                            Add Recipient
                        </Button>
                    </Grid>
                </Grid>
                {recipientNames.length > 0 && (
                    <List>
                        {recipientNames.map((name, index) => (
                            <ListItem key={index}>
                                <ListItemText primary={name} />
                                <IconButton onClick={() => removeRecipient(index)}>
                                    <DeleteIcon />
                                </IconButton>
                            </ListItem>
                        ))}
                    </List>
                )}
                <Button
                    variant="contained"
                    color="primary"
                    onClick={generateEmail}
                    fullWidth
                >
                    Generate Email
                </Button>
                {verificationError && <div className="error">{verificationError}</div>}
            </Paper>
        </Container>
    );
}

export default App;
