import React, { useState } from 'react';
import { Container, Paper, Typography, TextField, Button, Box } from '@mui/material';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [recipientNames, setRecipientNames] = useState([]);
  const [newRecipient, setNewRecipient] = useState('');
  const [generatedEmail, setGeneratedEmail] = useState('');

  const addRecipient = () => {
      if (newRecipient) {
          setRecipientNames([...recipientNames, newRecipient]);
          setNewRecipient('');
      }
  };

  const removeRecipient = (index) => {
      const updatedRecipientNames = [...recipientNames];
      updatedRecipientNames.splice(index, 1);
      setRecipientNames(updatedRecipientNames);
  };

  const generateEmail = async () => {
      // Prepare the data to send to the backend
      const data = {
          prompt: prompt,
      };

      // Make a POST request to your FastAPI backend
      const response = await fetch('http://localhost:8000/generate-email', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json',
          },
          body: JSON.stringify(data),
      });

      if (response.ok) {
          const responseData = await response.json();
          setGeneratedEmail(responseData.generated_email); // Update generatedEmail with data from the backend
      } else {
          // Handle errors here
          console.error('Failed to generate email');
      }
  };


    return (
        <Container maxWidth="sm" className="page-container">
            <Paper elevation={3} className="paper">
                <Typography variant="h4" gutterBottom className="heading">
                    Where knowledge begins
                </Typography>
                <Box className="input-area">
                    <TextField
                        id="prompt"
                        placeholder="Ask anything..."
                        variant="outlined"
                        fullWidth
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                        className="prompt-input"
                    />
                    <Button variant="contained" color="primary" onClick={generateEmail} className="send-button">
                        Copilot
                    </Button>
                </Box>
                {/* You can still include the GeneratedEmail component here if needed */}
            </Paper>
        </Container>
    );
}

export default App;
