import React, { useState } from 'react';
import './App.css';

function App() {
    const [prompt, setPrompt] = useState('');
    const [recipientNames, setRecipientNames] = useState([]);
    const [generatedEmails, setGeneratedEmails] = useState([]);
    const [verificationError, setVerificationError] = useState('');
    const [newRecipient, setNewRecipient] = useState('');

    const handleRecipientNameChange = (index, value) => {
        const updatedRecipientNames = [...recipientNames];
        updatedRecipientNames[index] = value;
        setRecipientNames(updatedRecipientNames);
    };

    const addRecipient = () => {
        if (newRecipient) {
            setRecipientNames([...recipientNames, newRecipient]);
            setNewRecipient('');
        }
    };

    const removeRecipient = (index) => {
        const updatedRecipientNames = [...recipientNames];
        updatedRecipientNames.splice(index, 1);
        setRecipientNames(updatedRecipientNames);
    };

    const generateEmail = () => {
        // Validate email addresses before generating emails
        if (recipientNames.some((name) => !isValidEmail(name))) {
            setVerificationError('Invalid email(s)');
            setGeneratedEmails([]);
            return;
        }

        // Use the OpenAI API to generate an email based on the prompt
        // You need to implement this part based on your project requirements

        // Mock generated email content for demonstration purposes
        const generatedEmailContents = recipientNames.map((name) => {
            return `Hello ${name},\n\n${prompt}`;
        });

        setGeneratedEmails(generatedEmailContents);
        setVerificationError('');
    };

    const isValidEmail = (email) => {
        // Basic email verification (you can enhance this validation)
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    return (
        <div className="container">
            <h1>Email Outreach Generator</h1>
            <div className="form">
                <div className="prompt">
                    <label htmlFor="prompt">Enter Your Prompt:</label>
                    <textarea
                        id="prompt"
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                        rows="4"
                        placeholder="Enter your email prompt"
                    />
                </div>
                <div className="recipient-inputs">
                    <div className="recipient-labels">
                        {recipientNames.map((name, index) => (
                            <div key={index} className="recipient-label">
                                <span>{name}</span>
                                <button onClick={() => removeRecipient(index)}>X</button>
                            </div>
                        ))}
                    </div>
                    <div className="add-recipient">
                        <input
                            type="text"
                            value={newRecipient}
                            onChange={(e) => setNewRecipient(e.target.value)}
                            placeholder="Add recipient's email"
                        />
                        <button onClick={addRecipient}>Add Recipient</button>
                    </div>
                </div>
                <button onClick={generateEmail}>Generate Email</button>
                {verificationError && <div className="error">{verificationError}</div>}
            </div>
            <div className="result">
                <h2>Generated Email:</h2>
                <div id="generatedEmail">
                    {generatedEmails.map((email, index) => (
                        <div key={index} className="generated-email">
                            {email}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
