import { Container, Paper, Button } from '@mui/material';
import PromptInput from './components/PromptInput';
import Header from './components/Header';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';
import GeneratedEmailPage from './components/GeneratedEmailPage';
import './App.css';

function Main({ onGenerateEmail }) {
  const [prompt, setPrompt] = useState('');
  const navigate = useNavigate();

  const generateEmail = async () => {
    // Replace with your logic to call the backend and generate the email
    const responseData = await onGenerateEmail(prompt);

    // Navigate to the new page after setting the state
    navigate('/generated-email');
  };

  return (
    <Container maxWidth="md" className="page-container">
      <Paper elevation={3} className="paper">
        <PromptInput prompt={prompt} setPrompt={setPrompt} />
        <div className="generate-button-div">
          <Button
            variant="contained"
            color="primary"
            onClick={generateEmail}
            className="generate-button"
            startIcon={<ArrowForwardIcon />}
          >
            Generate Email
          </Button>
        </div>
      </Paper>
    </Container>
  );
}

function App() {
  const [generatedEmail, setGeneratedEmail] = useState('');

  const handleGenerateEmail = async (prompt) => {
    // Logic to call the backend and generate the email
    // Simulating a backend call with a timeout
    const responseData = { generated_email: "Email content generated from the prompt." }; // Replace with actual response
    setGeneratedEmail(responseData.generated_email);
    return responseData;
  };

  return (
    <BrowserRouter>
      <Header />
      <Routes>
        <Route path="/" element={<Main onGenerateEmail={handleGenerateEmail} />} />
        <Route path="/generated-email" element={<GeneratedEmailPage emailContent={generatedEmail}/>}  />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
