import React, { useState } from 'react';
import { Container, Paper, Typography, Box, Button, IconButton, Icon } from '@mui/material';
import PromptInput from './components/PromptInput';
import RecipientList from './components/RecipientList';
import GeneratedEmail from './components/GeneratedEmail';
import './App.css';
import Header from './components/Header';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom'; // Import Router and related components
import GeneratedEmailPage from './GeneratedEmailPage'; // Import the new component

function App() {
    const [prompt, setPrompt] = useState('');
    const [recipientNames, setRecipientNames] = useState([]);
    const [newRecipient, setNewRecipient] = useState('');
    const [generatedEmail, setGeneratedEmail] = useState('');

    const addRecipient = () => {
        if (newRecipient) {
            setRecipientNames([...recipientNames, newRecipient]);
            setNewRecipient('');
        }
    };

    const removeRecipient = (index) => {
        const updatedRecipientNames = [...recipientNames];
        updatedRecipientNames.splice(index, 1);
        setRecipientNames(updatedRecipientNames);
    };

    const generateEmail = async () => {
        // Prepare the data to send to the backend
        const data = {
            prompt: prompt,
        };

        // Make a POST request to your FastAPI backend
        const response = await fetch('http://localhost:8000/generate-email', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        });

        if (response.ok) {
            const responseData = await response.json();
            setGeneratedEmail(responseData.generated_email); // Update generatedEmail with data from the backend
        } else {
            // Handle errors here
            console.error('Failed to generate email');
        }
    };

    return (
        <div>
            <Header />
            <Container maxWidth="md" className="page-container">
                <Paper elevation={3} className="paper">
                    <PromptInput prompt={prompt} setPrompt={setPrompt} />
                    <div className="generate-button-div">
                    <span className="generate-button-text">Generate Email</span> 
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={generateEmail}
                        className="generate-button"
                        startIcon={  // Add an icon and label to the left of the button
                            <IconButton color="inherit">
                                <ArrowForwardIcon /> {/* Use the ArrowForward icon */}
                            </IconButton>
                        }
                    ></Button>
                    <Link to="/generated-email">View Generated Email</Link>
                    </div>

                    <GeneratedEmail generatedEmail={generatedEmail} />
                        <Switch> {/* Define your routes using Switch */}
                            <Route path="/generated-email">
                                <GeneratedEmailPage generatedEmail={generatedEmail} />
                            </Route>
                        </Switch>

                </Paper>
            </Container>
        </div>
    );
}

export default App;
